          db.execute ("DROP TABLE IF EXISTS \"cmall_order_bought_goods\" CASCADE");
          db.execute ("DROP TABLE IF EXISTS \"cmall_order_recipient\" CASCADE");
          db.execute ("DROP TABLE IF EXISTS \"cmall_order\" CASCADE");
